package conditionals;

public class ifStatementRunner {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 25;
		if (i == 25) {
			System.out.println("Correct!");
		} else if (i == 24) {
			System.out.println("Almost!");
		} else {
			System.out.println("WRONG!");
		}

	}

}
